import React, {useEffect, useState} from "react";
import {data} from "../data";


const Card = () => {
  const [cities ,setCities] = useState(data);
  const [isShow,setIsShow] = useState(false)
  const eventHandler = (name) => {
    const newCities =cities.filter(item => item.name !== name);
    setCities(newCities)
  }

  const showElements = () => {
    setIsShow(!isShow)
  }
  useEffect(() => {
    window.addEventListener('click',showElements)
  },[isShow])

  return (

      <div>
        {cities.length ?
          <div>
            <h1>Our Tours</h1>
            {cities.map(city => {
              const {img, name, days, price, information} = city;
              return (
                <div className="card">
                  <div className="card-header">
                    <img src={img} alt=""/>
                  </div>
                  <div className="card-body">
                    <section><h6>{`Best Of ${name} in ${days} days`} </h6><span>{price}</span></section>
                    <p>{information}</p>
                    <button onClick={() => eventHandler(name)}> Not Interested</button>
                  </div>
                </div>
              )
            })}
          </div>
          :
          <div>
            <h1> No Tours Left </h1>
            <button onClick={showElements}>Refresh
            </button>
          </div>}
      </div>
  );
};


export default Card;
