import React, {useEffect, useState} from "react";
import {data} from "../data";
import {FiRefreshCw} from 'react-icons/fi'
import Loader from "react-loader-spinner";


const Tours = () => {
  const [tours, setTours] = useState(data);
  const [isLoading, setIsLoading] = useState(true);

  let slideIndex = 0;
  showSlides();

  function showSlides() {
    let i;
    const slides = document.getElementsByClassName("mySlides");
    const dots = document.getElementsByClassName("dot");
    for (i = 0; i < slides.length; i++) {
      slides[i].style.display = "none";
    }
    slideIndex++;
    if (slideIndex > slides.length) {slideIndex = 1}
    for (i = 0; i < dots.length; i++) {
      dots[i].className = dots[i].className.replace(" active", "");
    }
    slides[slideIndex-1].style.display = "block";
    dots[slideIndex-1].className += " active";
    setTimeout(showSlides, 2000);
  }



  const onClickDelete = (name) => {
    const newCities = tours.filter(item => item.name !== name);
    setTours(newCities)
  }

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false)
    }, 2000)

  }, []);

  if (isLoading) {
    return (
      <div className={"loader"}>
        <Loader
          type="radio"
          color="#00BFFF"
          height={100}
          width={100}
          timeout={3000} //3 secs
        />
      </div>
    )
  }


  return (
    <div>
      {tours.length ?
        <div className={"tours-page"}>
          <h1 className={"page-1-title"}>Our Tours</h1>
          <div className={"line"}/>
          <article className={"tours-list"}>
            {tours.map(tour => {
              const {img, name, days, price, information} = tour;
              return (
                <div key={name} className={"tour"}>
                  <div>
                    <div className="slideshow-container">

                      <div className="mySlides fade">
                        <div className="number-text">1 / 3</div>
                        <img src={img} alt={""}/>
                        <div className="text">Caption Text</div>
                      </div>

                      <div className="mySlides fade">
                        <div className="number-text">2 / 3</div>
                        <img src={img} alt={""}/>
                        <div className="text">Caption Two</div>
                      </div>

                      <div className="mySlides fade">
                        <div className="number-text">3 / 3</div>
                        <img src={img} alt={""}/>
                        <div className="text">Caption Three</div>
                      </div>

                    </div>
                    <br/>
                    <div style={{textAlign: "center"}}>
                      <span className="dot"/>
                      <span className="dot"/>
                      <span className="dot"/>
                    </div>
                  </div>
                  {/* <img className="tour-img" src={img} alt={`${name} Photo`}/>*/}
                  <div className="tour-data">
                    <section className={"tour-title"}>
                      <h2>{`Best Of ${name} in ${days} days`}</h2>
                      <h4><span style={{transform: "scale(1.1)"}}>$</span>{price}</h4>
                    </section>
                    <p>{information}</p>
                    <button className={"delete-button"} type="button"
                            onClick={() => onClickDelete(name)}>
                      Not Interested
                    </button>
                  </div>
                </div>
              )
            })}
          </article>

        </div>
        :
        <div className={"page-2"}>
          <h1 className={"page-2-title"}> No Tours Left </h1>
          <button className={"refresh-button"} onClick={() => setTours(data)}>
            <FiRefreshCw/> Refresh
          </button>
        </div>
      }
    </div>
  );
};


export default Tours;
