import React, {useState} from "react";
import {data} from "../data";

const Card = () => {
  const [cities, setCities] = useState(data)
  const eventHandler = (name) => {
    setCities(cities.filter(item => item.name !== name))
  }
  return (
    <div>
      {cities.length !== 0 &&
        <div>
          <h1>Our Tours</h1>
          {cities.map(city => {
            const {img, name, days, price, information} = city;
            return (
              <div className="card">
                <div className="card-header">
                  <img src={img} alt=""/>
                </div>
                <div className="card-body">
                  <section><h6>{`Best Of ${name} in ${days} days`} </h6><span>{price}</span></section>
                  <p>{information}</p>
                  <button onClick={() => eventHandler(name)}> Not Interested</button>
                </div>
              </div>
            )
          })}
        </div>}

      {cities.length === 0 && <Show/>}
    </div>
  );
};

const Show = () => {
  const [isShow, setIsShow] = useState(false);

  return (
    <div>
      <h1> No Tours Left </h1>
      <button onMouseOver={() => setIsShow(!isShow)}>Refresh
      </button>
    </div>
  );
};

export default Card;
