import React, {useEffect, useState} from 'react';
import {data} from "../data";

const Card = () => {
  const [cities, setCities] = useState(data);
  const [isLoading,setIsLoading] = useState(true)
  const [isError,setIsError] = useState(false)

  const eventHandler = (name) => {
    setCities(cities.filter(item => item.name !== name))
  }
  const getCities = async() => {
    const response = await fetch(data);
    const cities = await response.json();
    setCities(cities)

    ;
  }
  useEffect(() => {
    getCities();
  }, [])
  return (
    <div>
      {cities.length !== 0 &&
        <div>
          <h1>Our Tours</h1>
          {cities.map(state => {
            const {img, name, days, price, information} = cities;
            return (
              <div className="card">
                <div className="card-header">
                  <img src={img} alt=""/>
                </div>
                <div className="card-body">
                  <section><h6>{`Best Of ${name} in ${days} days`} </h6><span>{price}</span></section>
                  <p>{information}</p>
                  <button onClick={() => eventHandler(name)}> Not Interested</button>
                </div>
              </div>
            )
          })}
        </div>}

      {cities.length === 0 && <div><h1> No Tours Left </h1>
        <button onMouseOver={getCities}>Refresh
        </button>
      </div>}
    </div>


  );
};

export default Card;
