import React, {useEffect, useState} from "react";
import {data} from "../data";
import {FiRefreshCw} from 'react-icons/fi'
import Loader from "react-loader-spinner";


const Tours = () => {
  const [tours, setTours] = useState(data);
  const [isLoading, setIsLoading] = useState(true);

  const onClickDelete = (name) => {
    const newCities = tours.filter(item => item.name !== name);
    setTours(newCities)
  }

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false)
    }, 2000)

  }, []);

  if (isLoading) {
    return (
      <div>
        <Loader className="text-center"
                type="radio"
                color="#00BFFF"
                height={100}
                width={100}
                timeout={3000} //3 secs
        />
      </div>
    )
  }

  return (
    <div>
      {tours.length ?
        <div className={"tours-page"}>
          <h1>Our Tours</h1>
          <article className={"tours-list"}>
            {tours.map(tour => {
              const {img, name, days, price, information} = tour;
              return (
                <div className={"tour"}>
                  <div className={""}>
                    <img className="tour-img" src={img} alt={`${name} Photo`}/>
                    <div className="tour-data">
                      <section>
                        <h2 className="fs-5 fw-bold">{`Best Of ${name} in ${days} days`}</h2>
                        <span className="pt-1">{price}</span>
                      </section>
                      <p>{information}</p>
                      <button className=" btn-sm  btn-outline-danger" type="button"
                              onClick={() => onClickDelete(name)}>
                        Not Interested
                      </button>
                    </div>
                  </div>
                </div>
              )
            })}
          </article>

        </div>
        :
        <div>
          <h1> No Tours Left </h1>
          <button className="btn btn-sm btn-outline-success" onClick={() => setTours(data)}>
            <FiRefreshCw/> Refresh
          </button>
        </div>
      }
    </div>
  );
};


export default Tours;
