import React, {useEffect, useState} from "react";
import {data} from "../data";
import {FiRefreshCw} from 'react-icons/fi'
import Loader from "react-loader-spinner";


const Card = () => {
  const [cities, setCities] = useState(data);
  const [isLoading, setIsLoading] = useState(true);

  const eventHandler = (name) => {
    const newCities = cities.filter(item => item.name !== name);
    setCities(newCities)
  }

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false)
    }, 2000)

  }, []);

  if (isLoading) {
    return (
      <div>
        <Loader className="text-center"
          type="radio"
          color="#00BFFF"
          height={100}
          width={100}
          timeout={3000} //3 secs
        />
      </div>
    )
  }

  return (
    <div>
      {cities.length ?
        <div>
          <h1>Our Tours</h1>
          {cities.map(city => {
            const {img, name, days, price, information} = city;
            return (
              <div className="container">
                <div className="card ">
                  <img className="card-header" src={img} alt={`${name} Photo`}/>
                  <section><h6>{`Best Of ${name} in ${days} days`} </h6><span>{price}</span></section>
                  <p>{information}</p>
                  <div>
                    <button className="btn btn-sm  btn-block btn-outline-danger" type="button"
                            onClick={() => eventHandler(name)}> Not Interested
                    </button>
                  </div>
                </div>
              </div>

            )
          })}
        </div>
        :
        <div>
          <h1> No Tours Left </h1>
          <button className="btn btn-sm btn-outline-success" onClick={() => setCities(data)}><FiRefreshCw/> Refresh
          </button>
        </div>}
    </div>
  );
};


export default Card;
