import React, {useContext} from 'react';
import {CitiesContent} from "./Card";

const CardInformation = () => {
  const [cities,setCities] = useContext(CitiesContent)
  const eventHandler = (name) => {
    setCities(cities.filter(item => item.name !== name))
  }
  return (
      <div id="tours">
        <h1>Our Tours</h1>
        {cities.map(city => {
          const {img, name, days, price, information} = city;
          return (
            <div className="card">
              <div className="card-header">
                <img src={img} alt=""/>
              </div>
              <div className="card-body">
                <section><h6>{`Best Of ${name} in ${days} days`} </h6><span>{price}</span></section>
                <p>{information}</p>
                <button onMouseOver={() => eventHandler(name)}> Not Interested</button>
              </div>
            </div>
          )
        })}
      </div>
  );
};

export default CardInformation;
