import React, {useEffect, useState} from 'react';
import {data} from "../data";

const Card = () => {
  const [state, setState] = useState(data);
  const eventHandler = (name) => {
    setState(state.filter(item => item.name !== name))
  }

  return (
    <div>
      {state.length !== 0 &&
        <div>
          <h1>ourTour</h1>
          {state.map(state => {
            const {img, name, days, price, information} = state;
            return (
              <div className="card">
                <div className="card-header">
                  <img src={img} alt=""/>
                </div>
                <div className="card-body">
                  <section><h6>{`Best Of ${name} in ${days} days`} </h6><span>{price}</span></section>
                  <p>{information}</p>
                  <button onClick={() => eventHandler(name)}> Not Interested</button>
                </div>
              </div>
            )
          })}
        </div>}

      {state.length === 0 && <div><h1>No Tours</h1>
        <button onClick={() => {return(<div>
          <h1>ourTour</h1>
          {state.map(state => {
            const {img, name, days, price, information} = state;
            return (
              <div className="card">
                <div className="card-header">
                  <img src={img} alt=""/>
                </div>
                <div className="card-body">
                  <section><h6>{`Best Of ${name} in ${days} days`} </h6><span>{price}</span></section>
                  <p>{information}</p>
                  <button onClick={() => eventHandler(name)}> Not Interested</button>
                </div>
              </div>
            )
          })}
        </div>)}}>Refresh
        </button>
      </div>}
    </div>


  );
};

export default Card;
