import React, {useEffect, useState} from "react";
import {data} from "../data";

const Card = () => {
  const [cities, setCities] = useState(data)
  const [Show, setShow] = useState("");
  const eventHandler = (name) => {
    setCities(cities.filter(item => item.name !== name))
  }
  const reShow = () =>{
    // eslint-disable-next-line no-unused-expressions
    window.document.getElementById("tour").innerHTML
  }
  useEffect(() => {
    // eslint-disable-next-line no-restricted-globals
   addEventListener('mouseover',reShow)

  })
  return (
    <div>
      {cities.length ?
        <div id="tours">
          <h1>Our Tours</h1>
          {cities.map(city => {
            const {img, name, days, price, information} = city;
            return (
              <div className="card">
                <div className="card-header">
                  <img src={img} alt=""/>
                </div>
                <div className="card-body">
                  <section><h6>{`Best Of ${name} in ${days} days`} </h6><span>{price}</span></section>
                  <p>{information}</p>
                  <button onClick={() => eventHandler(name)}> Not Interested</button>
                </div>
              </div>
            )
          })}
        </div>

      :
        <div>
          <h1> No Tours Left </h1>
          <button onMouseOver={reShow}>Refresh
          </button>
       </div> }
    </div>
  );
};



export default Card;
